  const [data1,setdata1]=useState([])
  useEffect(()=>{
    getdata()
  })
  const getdata=async()=>{
    const response=await axios.post("http://localhost:3004/getadmindata")
    setdata1(response.data)
  }

  return (
    <div className=''>
        <p className=''>Admin</p>
        {<div className=''>
        {
              data1.map((element,index)=>{
                return(
                  <div className='' key={index}>
                    <div className=''>
